if lleg_time ~= 0 then if (time - lleg_time) > 60 then set(target,"left_leg",0) set(target,"left_leg_time",0) end end -- head local head = has(target,"head") local head_time = has(target,"head_time")
if head_time ~= 0 then if (time - head_time) > 60 then set(target,"head",0) set(target,"head_time",0) end end -- torso local torso = has(target,"torso") local torso_time = has(target,"torso_time")
if torso_time ~= 0 then if (time - torso_time) > 60 then set(target,"torso",0) set(target,"torso_time",0) end end end end

