ScriptGroup.script:
caravan = caravan or {}

caravan.t = caravan.t or {}
caravan.capture = caravan.capture or {}

caravan.commit = function(tablen)
	local tn = {"Crop","Livestock","Ore","Rock","Water","Fish","Lumber"}
	for i=1,#tn do
		caravan.capture[tn[i]] = caravan.capture[tn[i]] or {}
		if caravan.t[tn[i]] then
			caravan.capture[tn[i]][caravan.t.name] = caravan.t[tn[i]].count
		end
	end
end

merc = merc or {}
merc.collect = merc.collect or false merc.display = merc.display or false merc.table = merc.table or {} merc.hold = merc.hold or {} merc.stats = merc.stats or {}
merc.list = { "Mithmarenn Icerazor", -- "Larinen Beargrave", "Boylard Kingwrecker", "Jeyress Venomrazor", "Giltwen Bearender", "Cyrrailynd Firemincer", "Pyryalllyrr Zombiesword", }
merc.do_hold = function() if #merc.hold > 0 then local str = "" local party, hostile = "<sea_green>", "<violet_red>"
local action = merc.hold[1]
str = str.."\n<grey>" str = str..string.format("%4d",action[1])..": " if table.contains(merc.list,action[2]) then str = str..party else str = str..hostile end str = str..string.rpad( string.cut(string.format("%10s",action[2]),19), 20, " " ).." <grey>hit "
for i=2, #merc.hold do local tablen = merc.hold[i] if tablen[1] == "kill" then if i~=2 then str = str..string.rpad("\n",30," ") end str = str.."<orange_red>[ "..string.trim(tablen[2]).." ]" else if merc.stats[action[2]] then merc.stats[action[2]].hits = merc.stats[action[2]].hits + 1 merc.stats[action[2]].out = merc.stats[action[2]].out + tablen[5] end if merc.stats[tablen[1]] then merc.stats[tablen[1]].hit = merc.stats[tablen[1]].hit + 1 merc.stats[tablen[1]].dmg = merc.stats[tablen[1]].dmg + tablen[5] end if i~=2 then str = str..string.rpad("\n",32," ") end if table.contains(merc.list,tablen[1]) then str = str..party else str = str..hostile end str = str..string.rpad(string.cut(tablen[1],19),20," ") str = str.."<grey>("..string.format("%3d",tablen[4])..") " if table.contains(merc.list,tablen[1]) then str = str.."<red>" else str = str.."<dodger_blue>" end str = str.."-"..string.lpad(string.format("%3.1f",tablen[2]),4," ").."%<grey> / " str = str..string.lpad(string.format("%3.1f",tablen[3]),4," ") .."%" end end table.insert( merc.table, str ) merc.hold = {}
end end

Script.script:
rss = rss or {} rss.allow = rss.allow or true rss.interval = 300 rss.get_timer = rss.get_timer or "" rss.display_timer = rss.display_timer or "" rss.tablen = rss.tablen or {}
rss.feeds = { "http://rss.nytimes.com/services/xml/rss/nyt/World.xml", "http://feeds.lifehacker.com.au/lifehackeraustralia", "http://feeds2.feedburner.com/TheArtOfManliness", }
function rss.get() if rss.allow then rss.locale = getMudletHomeDir().."/rss"
if not downloadFile then cecho("\nYour version of Mudlet doesn't support downloading files - please upgrade to 2.0+") else local feed = rss.feeds[1] cecho("\n<red>Downloading RSS file from "..feed.."<gray>") downloadFile( rss.locale, feed )
table.insert( rss.feeds, feed ) table.remove( rss.feeds, 1 ) end end end
function rss.display() if #rss.tablen > 0 then
local tablen = rss.tablen[1] local head = tablen[1] local link = tablen[2] cecho("[<cadet_blue>NEWS<gray>]: "..head.." (") echoLink("Click for more info:", [[openUrl("]]..link..[[")]], head) cecho(")")
table.remove( rss.tablen, 1 )
killTimer(rss.display_timer) rss.display_timer = tempTimer( rss.interval, function() rss.display() end ) end end
function rss.stop() killTimer(rss.display_timer) killTimer(rss.get_timer) cecho("\nStopped RSS Feeds.") end

